import React from 'react';
import styled from "styled-components";

const COLORS = {
    primaryDark: "#115b4c",
    primaryLight: "B6EDC8",
}

const MenuLabel = styled.label`
background-color: red;
position:fixed;
top: 18rem;
left: 5rem;
border-radius: 50%;
height: 3rem;
width: 3rem;
cursor: pointer;
z-index: 1000;
text-align: center;
`;

const NavBackground = styled.div`
position: fixed;
top: 6.5rem;
left: 6.5rem;
background-image: radial-gradient(
    ${COLORS.primaryDark},
    ${COLORS.primaryLight}
);
height: 6rem;
width: 6rem;
border-radius: 50%;
`;


const HamburgerMenu = () => {
    return (
        <>
            <MenuLabel htmlFor='navi-toggle'> Menu </MenuLabel>
            <NavBackground> &nbsp; </NavBackground>
        </>
    );
};

export default HamburgerMenu;
